# Title:  Question 4
# Author: Kai Toledano
# Dedcription: This code job is to recive a number from the user, and to make the user guess the said number, while doing so the program will notify the user on whether he got the correct answear or not on digit to digit basis.
# Input: three DIFFERENT digit long number.
# Output: a note that is made from trhee letters that mark's the user's guessing outcome, or error message otherwise.
# Filename: Question4
# Data: 08\08\2021
############################### Data segment ################################################################################################################################################################################################   
.data
     bool:          .space 4
     guess:         .space 4
     prompt1:       .asciiz "\nPlease enter 3 different digit in range 0 <= x <= 9."
     prompt2:       .asciiz "\n\nThe number that need to be guessed is:  "   
     prompt3:       .asciiz "\nGuess my number\n"
     prompt4:       .asciiz "n\nCorrect guess! Enter y for another game, or enter n to exit.\n"
     prompt_error:  .asciiz "\n\nWrong input, try again.\n"
     print_b:       .asciiz "b"  # For currect guess of a digit -> at least one currect guess.
     print_p:       .asciiz "p"  # For worng guess of a digit -> at least one wrong guess.
     print_n:       .asciiz "n"  # For three wrong guesses and other uses.
############################### Code segment ################################################################################################################################################################################################       
.text
.globl main

   # Start of program.

main:                  la $a0, bool                    # Gets bool array address.
                       jal get_number                  # Gets the number for bool.
                       li $v0, 4
                       la $a0, prompt2                 # Prints the number inputed by the user.
                       syscall
                       
                       li $v0, 4                      
                       la $a0, bool
                       syscall
     
goto_get_guess:        la  $a0, bool
                       la  $a1, guess                 # The second array which will hold the number enterd by the user thats need to be gusseted.
                       jal get_guess                  # Gets the said number.
                       bne $v0, -1, goto_get_guess
               
another_round:         li $v0, 4
                       la $a0, prompt4                # After the game is finished, ask the user if he want another game, or to exit the program.
                       syscall
                               
                       li $v0, 12
                       syscall
             
                       beq $v0, 'y', main            # This is the option to paly again.
                       beq $v0, 'n', exit            # This is the option to exit the program.
                       li  $v0, 4
                       la  $a0, prompt_error         # If the user had enterd an invalid input, the program will issue an error message.
                       syscall

                       j another_round
               
exit:                  li $v0, 10                    # End of program.
                       syscall                                    
               
get_number:            move $t0, $a0                 # Receive the first number from the user.
                       li   $t1, 0
                       li   $t2, 0
                       li   $v0, 4
                       la   $a0, prompt1
                       syscall
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                         
input_digit:           li $v0, 12
                       syscall
               
                       blt $v0, '0', invalid_input   # Check if the received number is in valid range.
                       bgt $v0, '9', invalid_input
                       
                       j valid_digit                 # By jumping to valid_digit.
               
invalid_input:         li $v0, 4
                       la $a0, prompt_error           # If the number is invalid, print error message.
                       syscall
                       
                       j input_digit
               
valid_digit:           bne  $t1, $zero, check_unique_loop    # After we took sure that we got a valid digit, jump to the loop.
                       move $t1, $v0
                       j save_input
               
check_unique_loop:     beq  $t1, $v0, invalid_input         # Check if the digits that make the number are unique.
                       bne  $t2, $zero, last_digit
                       move $t2, $v0
                       
save_input:            sb   $v0, 0($t0)                     # Save the number in memory.
                       addi $t0, $t0, 1
                       j input_digit
               
last_digit:            beq $t2, $v0, invalid_input
                       sb  $v0, 0($t0)
                       jr  $ra
               
get_guess:             move $t0, $a0
                       move $t1, $a1
                       j start_guessing
              
error_guess:           li $v0, 4
                       la $a0, prompt_error                   # Print error messages in case of invalid data.
                       syscall
  
start_guessing:        li $v0, 4                              # Here the program will get the results from the compare procedure the will print error message in case invalid data.
                       la $a0, prompt3
                       syscall
 
                       li   $v0, 8
                       move $a0, $t1
                       li   $a1, 4
                       syscall
           
                       lb $t3, 0($t1)                            # First digit to be gusseted.
                       lb $t4, 1($t1)                            # Second digit to be gusseted.
                       lb $t5, 2($t1)                            # Third digit to be gusseted.
           
                       blt $t3, '0', error_guess                 #################################################
                       bgt $t3, '9', error_guess                 #                                               #
                       blt $t4, '0', error_guess                 # Check that $t3, $t4, $t5 are in range of 0-9. #
                       bgt $t4, '9', error_guess                 #                                               #
                       blt $t5, '0', error_guess                 #                                               #
                       bgt $t5, '9', error_guess                 #################################################
            
                       beq $t3, $t4, error_guess                 #           
                       beq $t3, $t5, error_guess             # Check if all the digit are unique.   
                       beq $t4, $t5, error_guess                 #
                                                                
                       move $a0, $t0                             # Seting the digits and calling the compare procedure.
                       move $a1, $t1
                       addi $sp, $sp, -4
                       sw   $ra, 0($sp)
                       jal compare
            
                       lw   $ra, 0($sp)
                       addi $sp, $sp, 4
                       jr   $ra
                        
compare:               move $t0, $a0              # The compare procedure will compare the digits of the two numbers (compare the digits of the two numbers in bool and guess arrays with each other), and increment the bool counter and print a guess result letter upon successfull check.
                       move $t1, $a1
                       lb   $t2, 0($t0)
                       lb   $t3, 1($t0)
                       lb   $t4, 2($t0)
                       lb   $t5, 0($t1)
                       lb   $t6, 1($t1)
                       lb   $t7, 2($t1)      
                       li   $t8, 0
                       li   $t9, 0         
                       bne  $t2, $t5, test_2nd_bool
                       addi $t8, $t8, 1
                       li   $v0, 4
                       la   $a0, print_b
                       syscall
            
test_2nd_bool:         bne $t3, $t6, test_3rd_bool     
                       li  $v0, 4
                       la  $a0, print_b
                       syscall 
                                                  
                       addi $t8, $t8, 1
                    
test_3rd_bool:         bne  $t4, $t7, phase_1
                       addi $t8, $t8, 1
                       li   $v0, 4
                       la   $a0, print_b
                       syscall
                   
                       bne $t8, 3, phase_1
                       li  $v0, -1
                       j end_procedure
                   
phase_1:               bne  $t2, $t6, phase_2               # A set of checks on the numbers, resulting in printing the user's guessing outcome.
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                   
phase_2:               bne  $t2, $t7, phase_3
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                   
phase_3:               bne  $t3, $t5, phase_4
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                   
phase_4:               bne  $t3, $t7, phase_5
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                   
phase_5:               bne  $t4, $t5, phase_6
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     
phase_6:               bne  $t4, $t6, other
                       addi $t9, $t9, 1
                       li   $v0, 4
                       la   $a0, print_p
                       syscall
                   
other:                 bne  $t8, $zero, end_procedure             # Catch unknown cases.
                       bne  $t9, $zero, end_procedure
                       move $v0, $zero
                       li   $v0, 4
                       la   $a0, print_n
